void img_process::thread_hdltv_gen() {
    const char* dump_tv = std::getenv("AP_WRITE_TV");
    if (!(dump_tv && string(dump_tv) == "on")) return;

    wait();

    mHdltvinHandle << "[ " << endl;
    mHdltvoutHandle << "[ " << endl;
    int ap_cycleNo = 0;
    while (1) {
        wait();
        const char* mComma = ap_cycleNo == 0 ? " " : ", " ;
        mHdltvinHandle << mComma << "{"  <<  " \"ap_rst_n\" :  \"" << ap_rst_n.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"ap_start\" :  \"" << ap_start.read() << "\" ";
        mHdltvoutHandle << mComma << "{"  <<  " \"ap_done\" :  \"" << ap_done.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"ap_idle\" :  \"" << ap_idle.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"ap_ready\" :  \"" << ap_ready.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"rgb_data_in_dout\" :  \"" << rgb_data_in_dout.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"rgb_data_in_empty_n\" :  \"" << rgb_data_in_empty_n.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"rgb_data_in_read\" :  \"" << rgb_data_in_read.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"rgb_data_out_din\" :  \"" << rgb_data_out_din.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"rgb_data_out_full_n\" :  \"" << rgb_data_out_full_n.read() << "\" ";
        mHdltvoutHandle << " , " <<  " \"rgb_data_out_write\" :  \"" << rgb_data_out_write.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"isobelInvert\" :  \"" << isobelInvert.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"iminsobelSensitivity\" :  \"" << iminsobelSensitivity.read() << "\" ";
        mHdltvinHandle << " , " <<  " \"imaxsobelSensitivity\" :  \"" << imaxsobelSensitivity.read() << "\" ";
        mHdltvinHandle << "}" << std::endl;
        mHdltvoutHandle << "}" << std::endl;
        ap_cycleNo++;
    }
}

